// src/components/Sidebar.js
import React, { useRef, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import ''
import icon from '../assets/hospital.png';
import WebGazerWrapper from '../hooks/WebGazerWrapper';

const Sidebar = () => {
  const location = useLocation();
  const categoriesRef = useRef([]);
  const [gazingButton, setGazingButton] = useState(null);

  const categories = [
    { id: 'หน้าหลัก', label: 'หน้าหลัก' },
    { id: 'พยัญชนะ', label: 'พยัญชนะ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  const handleGaze = (prediction) => {
    categoriesRef.current.forEach((button, index) => {
      if (button) {
        const rect = button.getBoundingClientRect();
        const x = prediction.x;
        const y = prediction.y;

        if (x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom) {
          setGazingButton(button);
        }
      }
    });
  };

  useEffect(() => {
    if (gazingButton) {
      const timer = setTimeout(() => {
        if (gazingButton) {
          gazingButton.click();
        }
      }, 1000); // 1 วินาที

      return () => clearTimeout(timer);
    }
  }, [gazingButton]);

  return (
    <div className="sidebar">
      <WebGazerWrapper onGaze={handleGaze} />
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          ref={(el) => {
            if (el) {
              categoriesRef.current[index] = el;
            }
          }}
        >
          <div className="loading-bar"></div> {/* แถบโหลด */}
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
