// src/components/WebGazerWrapper.js
import React, { useEffect } from 'react';
import webgazer from 'webgazer';

const WebGazerWrapper = ({ onGaze }) => {
  useEffect(() => {
    webgazer.setRegression('ridge')
      .setTracker('clmtrackr')
      .begin()
      .showPredictionPoints(false)
      .showFaceOverlay(false)
      .showFacePoints(false)
      .showVideo(true);

    const handleGaze = () => {
      webgazer.getGazePrediction().then(prediction => {
        if (prediction) {
          onGaze(prediction);
        }
      });
    };

    const interval = setInterval(handleGaze, 100); // เช็คทุก 100ms

    return () => {
      clearInterval(interval);
      webgazer.end();
    };
  }, [onGaze]);

  return null;
};

export default WebGazerWrapper;
