// src/components/Sidebar.js
import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sidebar.css';
import icon from '../assets/hospital.png';
import WebGazerSetup from '../components/WebGazerSetup';

const Sidebar = () => {
  const location = useLocation();
  const [gazeTarget, setGazeTarget] = useState(null);

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  const handleGaze = (data) => {
    if (data && gazeTarget && Date.now() - gazeTarget.time > 1000) {
      gazeTarget.action();
      setGazeTarget(null);
    }
  };

  return (
    <div className="sidebar">
      <WebGazerSetup onGaze={handleGaze} />
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category) => (
        <Link 
          key={category.id} 
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          onMouseEnter={() => setGazeTarget({ time: Date.now(), action: () => window.location.href = `/category/${category.id}` })}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
