import React, { useState, useContext, useEffect, useMemo } from 'react';
import './HomePage.css';
import { GazeContext } from '../App';

const HomePage = () => {
  const [message, setMessage] = useState('');
  const { gazeTimers } = useContext(GazeContext);
  const suggestions = useMemo(() => ['สวัสดี', 'หิวข้าว', 'เข้าห้องน้ำ', 'สบายดีไหม', 'ขอบคุณ', 'ขอโทษ', 'คิดถึง', 'รัก', 'ไปไหน'], []);

  useEffect(() => {
    suggestions.forEach((_, index) => {
      gazeTimers.current[`suggestion-button-${index}`] = { isGazing: false, timer: null };
    });
    gazeTimers.current['send-button'] = { isGazing: false, timer: null };

    const timersCopy = { ...gazeTimers.current };

    return () => {
      suggestions.forEach((_, index) => {
        if (timersCopy[`suggestion-button-${index}`]?.timer) {
          clearTimeout(timersCopy[`suggestion-button-${index}`].timer);
        }
      });
      if (timersCopy['send-button']?.timer) {
        clearTimeout(timersCopy['send-button'].timer);
      }
    };
  }, [suggestions, gazeTimers]);

  const handleSend = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5001/messages', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: message }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const result = await response.json();
      console.log('Message sent:', result);
      setMessage('');
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  };

  return (
    <div className="home-page">
      <div className="message-input">
        <input 
          type="text" 
          value={message} 
          onChange={(e) => setMessage(e.target.value)} 
          placeholder="พิมพ์ข้อความ"
        />
        <button id="send-button" onClick={handleSend}>ตกลง</button>
      </div>
      <div className="suggestions">
        {suggestions.map((suggestion, index) => (
          <button 
            key={index}
            id={`suggestion-button-${index}`}
            onClick={() => setMessage(suggestion)}
            className="suggestion-button"
          >
            {suggestion}
          </button>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
