import React, { useCallback } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWebGazerContext } from '../hooks/WebGazerContext'; // นำเข้า useWebGazerContext
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const { webgazerReady, webgazerInstance } = useWebGazerContext();

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  const handleGaze = useCallback((data) => {
    if (!webgazerReady || !data) return;

    const { x, y } = data;
    const buttons = document.querySelectorAll('.category-button');
    buttons.forEach(button => {
      const rect = button.getBoundingClientRect();
      if (x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom) {
        button.classList.add('gazing');
        button.click();
      } else {
        button.classList.remove('gazing');
      }
    });
  }, [webgazerReady]);

  useEffect(() => {
    if (webgazerInstance.current) {
      webgazerInstance.current.setGazeListener(handleGaze);
    }
  }, [handleGaze]);

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category) => (
        <Link 
          key={category.id} 
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
