import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useWebGazerContext } from '../hooks/WebGazerContext'; // ตรวจสอบเส้นทางให้ถูกต้อง
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const navigate = useNavigate(); // ใช้ navigate เพื่อเปลี่ยนเส้นทาง
  const { webgazerInstance, webgazerReady } = useWebGazerContext();
  const categoriesRef = useRef([]);
  const [gazingAt, setGazingAt] = useState(null);
  const gazeTimeout = useRef(null);

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  const handleGaze = useCallback((data) => {
    if (!data) return;

    const { x, y } = data;
    let gazedButton = null;

    categoriesRef.current.forEach((ref, index) => {
      if (ref) {
        const rect = ref.getBoundingClientRect();
        const isWithinButton = x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom;
        if (isWithinButton) {
          ref.classList.add('gazing');
          if (!gazedButton) gazedButton = { ref, index };
        } else {
          ref.classList.remove('gazing');
        }
      }
    });

    if (gazedButton) {
      if (gazingAt !== gazedButton.ref) {
        setGazingAt(gazedButton.ref);
        if (gazeTimeout.current) {
          clearTimeout(gazeTimeout.current);
        }
        gazeTimeout.current = setTimeout(() => {
          console.log('Gaze navigate to:', categories[gazedButton.index].label);
          navigate(`/category/${categories[gazedButton.index].id}`);
          setGazingAt(null);
        }, 1000);
      }
    } else {
      setGazingAt(null);
      if (gazeTimeout.current) {
        clearTimeout(gazeTimeout.current);
        gazeTimeout.current = null;
      }
    }
    // eslint-disable-next-line
  }, [gazingAt, navigate]);

  useEffect(() => {
    if (webgazerReady && webgazerInstance.current) {
      const instance = webgazerInstance.current;
      instance.setGazeListener(handleGaze);

      return () => {
        if (instance) {
          instance.clearGazeListener();
        }
      };
    }
  }, [handleGaze, webgazerReady, webgazerInstance]);

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          ref={(el) => (categoriesRef.current[index] = el)}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
