import React, { useState, useCallback, useEffect } from 'react';
import './HomePage.css';
import alertIcon from '../assets/bell.png'; 
import { useWebGazerContext } from '../hooks/WebGazerContext';

const HomePage = () => {
  const [message, setMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const { webgazerInstance, gazeTimers } = useWebGazerContext();

  const suggestions = ['สวัสดี', 'หิวข้าว', 'เข้าห้องน้ำ', 'สบายดีไหม', 'ขอบคุณ', 'ขอโทษ', 'คิดถึง', 'รัก', 'ไปไหน'];

  const handleSend = async () => {
    // ... (ไม่เปลี่ยนแปลง)
  };

  const handleAlertClick = () => {
    // ... (ไม่เปลี่ยนแปลง)
  };

  const handlePopupClose = () => {
    // ... (ไม่เปลี่ยนแปลง)
  };

  const handleConfirmAlert = async () => {
    // ... (ไม่เปลี่ยนแปลง)
  };

  const handleGaze = useCallback((data, timestamp, timers) => {
    const { x, y } = data;
    const buttons = document.querySelectorAll('.suggestion-button, .alert-icon');

    buttons.forEach(button => {
      const rect = button.getBoundingClientRect();
      const isGazing = 
        x >= rect.left &&
        x <= rect.right &&
        y >= rect.top &&
        y <= rect.bottom;

      if (isGazing) {
        button.classList.add('gazing');
        if (!timers[button.innerText]) {
          timers[button.innerText] = timestamp;
        } else if (timestamp - timers[button.innerText] > 1000) { // 1 second
          if (button.classList.contains('alert-icon')) {
            handleAlertClick();
          } else {
            setMessage(button.innerText);
          }
          delete timers[button.innerText];
        }
      } else {
        button.classList.remove('gazing');
        delete timers[button.innerText];
      }
    });
  }, [setMessage, handleAlertClick]);

  useEffect(() => {
    const instance = webgazerInstance.current;
    if (instance) {
      instance.setGazeListener((data, timestamp) => handleGaze(data, timestamp, gazeTimers.current));
    }

    return () => {
      if (instance) {
        instance.clearGazeListener();
      }
    };
  }, [handleGaze, webgazerInstance, gazeTimers]);



  return (
    <div className="home-page">
      <div className="message-input">
        <input 
          type="text" 
          value={message} 
          onChange={(e) => setMessage(e.target.value)} 
          placeholder="พิมพ์ข้อความ"
        />
        <button onClick={handleSend}>ตกลง</button>
      </div>
      <div className="suggestions">
        {suggestions.map((suggestion, index) => (
          <button 
            key={index} 
            onClick={() => setMessage(suggestion)}
            className="suggestion-button"
          >
            {suggestion}
          </button>
        ))}
      </div>
      <img 
        src={alertIcon} 
        alt="alert" 
        className="alert-icon" 
        onClick={handleAlertClick} 
      />
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <p>ต้องการเรียกผู้ดูแลหรือไม่?</p>
            <button onClick={handleConfirmAlert}>ใช่</button>
            <button onClick={handlePopupClose}>ไม่ใช่</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
