import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import CategoryPage from './pages/CategoryPage';
import { WebGazerProvider, useWebGazerContext } from './hooks/WebGazerContext'; // ใช้ WebGazerProvider
import './App.css';

const AppContent = () => {
  const { webgazerInstance } = useWebGazerContext();

  useEffect(() => {
    const instance = webgazerInstance.current;

    if (instance) {
      instance.begin();
    }

    return () => {
      if (instance) {
        instance.end();
      }
    };
  }, [webgazerInstance]);

  return (
    <div className="app-container">
      <Sidebar />
      <div className="main-content">
        <Routes>
          <Route path="/" element={<CategoryPage id="หน้าหลัก" />} />
          <Route path="/category/:id" element={<CategoryPage />} />
        </Routes>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <WebGazerProvider>
      <Router>
        <AppContent />
      </Router>
    </WebGazerProvider>
  );
};

export default App;
