import React, { useState, useEffect, useRef, useCallback, createContext } from 'react';
import Button from '../components/Button';
import Sidebar from '../components/Sidebar';
import CalibrationPoint from '../components/CalibrationPoint';
import Header from '../components/Header';
import './HomePage.css';

export const GazeContext = createContext();

function HomePage() {
  const [selectedCategory, setSelectedCategory] = useState("ก-ซ");
  const [calibrating, setCalibrating] = useState(false);
  const [calibrationPoints, setCalibrationPoints] = useState([]);
  const [clickCounts, setClickCounts] = useState(Array(19).fill(0));
  const [inputText, setInputText] = useState('');
  const [webgazerReady, setWebgazerReady] = useState(false);
  const webgazerInstance = useRef(null);
  const gazeTimers = useRef({});

  const categories = ["ก-ซ", "ญ-ถ", "ท-ม", "ย-ฮ", "สระ", "วรรณยุกต์"];
  const buttons = {
    "ก-ซ": ["ก", "ข", "ฃ", "ค", "ต", "ฆ", "ง", "จ", "ฉ", "ช", "ซ"]
  };

  const handleButtonClick = useCallback((text) => {
    if (!calibrating) {
      setInputText(prevText => prevText + text);
    }
  }, [calibrating]);

  const handleGaze = useCallback((data) => {
    if (data == null || calibrating) return;

    const { x, y } = data;
    Object.keys(gazeTimers.current).forEach(buttonId => {
      const button = document.getElementById(buttonId);
      if (button) {
        const rect = button.getBoundingClientRect();
        if (
          x >= rect.left &&
          x <= rect.right &&
          y >= rect.top &&
          y <= rect.bottom
        ) {
          if (!gazeTimers.current[buttonId].isGazing) {
            gazeTimers.current[buttonId].isGazing = true;
            gazeTimers.current[buttonId].timer = setTimeout(() => {
              handleButtonClick(button.innerText);
            }, 1000); // 1 second gaze to trigger click
          }
        } else {
          gazeTimers.current[buttonId].isGazing = false;
          if (gazeTimers.current[buttonId].timer) {
            clearTimeout(gazeTimers.current[buttonId].timer);
          }
        }
      }
    });
  }, [calibrating, handleButtonClick]);

  useEffect(() => {
    const initWebgazer = async () => {
      try {
        await window.webgazer.setRegression('ridge').setTracker('clmtrackr')
          .setGazeListener(handleGaze).begin();
        webgazerInstance.current = window.webgazer;
        setWebgazerReady(true);

        window.webgazer.showVideo(true);
        window.webgazer.showFaceOverlay(true);
        window.webgazer.showFaceFeedbackBox(true);

      } catch (error) {
        console.error('Failed to initialize webgazer:', error);
      }
    };

    initWebgazer();

    return () => {
      if (webgazerInstance.current && typeof webgazerInstance.current.end === 'function') {
        try {
          webgazerInstance.current.end();
        } catch (error) {
          console.error('Error while ending webgazer:', error);
        }
      }
    };
  }, [handleGaze]);

  const startCalibration = () => {
    if (webgazerInstance.current) {
      webgazerInstance.current.clearData();
      webgazerInstance.current.showFaceOverlay(false);
      setCalibrating(true);
      setCalibrationPoints(generateCalibrationPoints());
      setClickCounts(Array(19).fill(0));
    }
  };

  const stopCalibration = () => {
    if (webgazerInstance.current) {
      webgazerInstance.current.showFaceOverlay(false);
    }
    setCalibrating(false);
    setCalibrationPoints([]);
  };

  const generateCalibrationPoints = () => {
    const positions = [
      { x: 10, y: 20 }, { x: 10, y: 35 }, { x: 10, y: 50 }, { x: 10, y: 65 }, { x: 10, y: 80 },
      { x: 10, y: 95 }, { x: 30, y: 50 }, { x: 50, y: 50 }, { x: 70, y: 50 }, { x: 90, y: 50 },
      { x: 30, y: 65 }, { x: 50, y: 65 }, { x: 70, y: 65 }, { x: 90, y: 65 }, { x: 30, y: 85 },
      { x: 50, y: 85 }, { x: 70, y: 85 }, { x: 95, y: 30 }, { x: 87, y: 3 },
    ];
    return positions.map(pos => ({ x: `${pos.x}%`, y: `${pos.y}%` }));
  };

  const handleCalibrationClick = (index, e) => {
    if (webgazerInstance.current) {
      const { clientX, clientY } = e;
      webgazerInstance.current.recordScreenPosition(clientX, clientY, 'click');
      const newClickCounts = [...clickCounts];
      newClickCounts[index] += 1;
      setClickCounts(newClickCounts);

      if (newClickCounts[index] >= 3) {
        const newCalibrationPoints = [...calibrationPoints];
        newCalibrationPoints[index].color = 'yellow';
        setCalibrationPoints(newCalibrationPoints);
      }
    }
  };

  const handleSubmit = () => {
    console.log('Submitted text:', inputText);
    setInputText('');
  };

  if (!webgazerReady) {
    return <div>กำลังโหลด WebGazer...</div>;
  }

  return (
    <GazeContext.Provider value={{ gazeTimers, calibrating }}>
      <div className="home-page">
        <Sidebar categories={categories} setSelectedCategory={setSelectedCategory} selectedCategory={selectedCategory} />
        <div className="main-content">
          <Header />
          <div className="input-container">
            <input 
              type="text" 
              placeholder="พิมพ์ข้อความ" 
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
            />
            <button className="submit-button" onClick={handleSubmit}>ตกลง</button>
          </div>
          <div className="buttons-container">
            {buttons[selectedCategory].map((text, index) => (
              <Button key={index} text={text} onClick={() => handleButtonClick(text)} id={`button-${index}`} />
            ))}
          </div>
          <div className="calibration-container">
            <button onClick={startCalibration}>เริ่มคาลิเบรต</button>
            <button onClick={stopCalibration}>หยุดคาลิเบรต</button>
          </div>
          {calibrating && (
            <div className="calibration-points">
              {calibrationPoints.map((point, index) => (
                <CalibrationPoint 
                  key={index}
                  point={point}
                  index={index}
                  handleCalibrationClick={handleCalibrationClick}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </GazeContext.Provider>
  );
}

export default HomePage;
