// components/Sidebar.js
import React, { useEffect, useRef, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWebGazerContext } from '../hooks/WebGazerContext'; // ตรวจสอบเส้นทางให้ถูกต้อง
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const { webgazerInstance } = useWebGazerContext();
  const [isCalibrating, setIsCalibrating] = useState(false);
  const categoriesRef = useRef([]);

  useEffect(() => {
    const instance = webgazerInstance.current;
    const currentRefs = categoriesRef.current;

    if (instance) {
      if (isCalibrating) {
        // เพิ่มการติดตามการคลิกบนจุด calibration
        currentRefs.forEach((ref) => {
          if (ref) {
            instance.addCalibrationListener(ref, () => {
              // ทำการ calibrate ที่จุดที่คลิก
              instance.calibrate(ref);
            });
          }
        });
      } else {
        // เพิ่มการติดตามการจ้องมอง (gaze) สำหรับแต่ละปุ่ม
        currentRefs.forEach((ref) => {
          if (ref) {
            instance.addGazeListener(ref, () => {
              ref.click(); // คลิกปุ่มเมื่อถูกจ้องมอง
            });
          }
        });
      }
    }

    return () => {
      if (instance) {
        currentRefs.forEach((ref) => {
          if (ref) {
            if (isCalibrating) {
              // ลบการติดตามการคลิกบนจุด calibration
              instance.removeCalibrationListener(ref);
            } else {
              // ลบการติดตามการจ้องมอง (gaze) สำหรับแต่ละปุ่ม
              instance.removeGazeListener(ref);
            }
          }
        });
      }
    };
  }, [webgazerInstance, isCalibrating]);

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          ref={(el) => (categoriesRef.current[index] = el)}
        >
          {category.label}
        </Link>
      ))}
      <button onClick={() => setIsCalibrating(!isCalibrating)} className="calibrate-button">
        {isCalibrating ? 'Stop Calibration' : 'Start Calibration'}
      </button>
    </div>
  );
};

export default Sidebar;
