// src/components/CalibrationOverlay.js
import React, { useState, useEffect } from 'react';
import './CalibrationOverlay.css';

const generateCalibrationPoints = () => {
  const positions = [
    { x: 10, y: 20 }, { x: 10, y: 35 }, { x: 10, y: 50 }, { x: 10, y: 65 }, { x: 10, y: 80 },
    { x: 10, y: 95 }, { x: 30, y: 50 }, { x: 50, y: 50 }, { x: 70, y: 50 }, { x: 90, y: 50 },
    { x: 30, y: 65 }, { x: 50, y: 65 }, { x: 70, y: 65 }, { x: 90, y: 65 }, { x: 30, y: 85 },
    { x: 50, y: 85 }, { x: 70, y: 85 }, { x: 95, y: 30 }, { x: 87, y: 3 },
  ];
  return positions.map(pos => ({ x: `${pos.x}%`, y: `${pos.y}%` }));
};

const CalibrationOverlay = ({ isCalibrationMode, onCalibrationComplete }) => {
  const [calibratedPoints, setCalibratedPoints] = useState(0);
  const points = generateCalibrationPoints();

  useEffect(() => {
    if (calibratedPoints >= 3) {
      onCalibrationComplete();
    }
  }, [calibratedPoints, onCalibrationComplete]);

  const handleCalibrationClick = (e) => {
    if (!isCalibrationMode) return;
    setCalibratedPoints(prev => prev + 1);
  };

  return (
    <div className="calibration-overlay">
      {points.map((point, index) => (
        <div
          key={index}
          className="calibration-point"
          style={{ left: point.x, top: point.y }}
          onClick={handleCalibrationClick}
        />
      ))}
    </div>
  );
};

export default CalibrationOverlay;
