import React, { useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useWebGazerContext } from '../contexts/WebGazerContext'; // นำเข้า useWebGazerContext
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { webgazerInstance } = useWebGazerContext(); // ดึงค่า webgazerInstance
  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  useEffect(() => {
    if (!webgazerInstance.current) return;

    const buttons = document.querySelectorAll('.category-button');

    buttons.forEach((button) => {
      const categoryId = button.getAttribute('data-category-id');

      const gazeListener = (data, timestamp) => {
        if (data == null) return;

        const x = data.x;
        const y = data.y;
        const rect = button.getBoundingClientRect();

        if (
          x >= rect.left && x <= rect.right &&
          y >= rect.top && y <= rect.bottom
        ) {
          button.classList.add('gazed');
          setTimeout(() => {
            navigate(`/category/${categoryId}`);
          }, 1000); // รอ 1 วินาทีแล้วทำการ navigate ไปยังหมวดหมู่ที่เลือก
        } else {
          button.classList.remove('gazed');
        }
      };

      webgazerInstance.current.setGazeListener(gazeListener).begin();
    });

    return () => {
      webgazerInstance.current.clearGazeListener();
    };
  }, [webgazerInstance, navigate]);

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category) => (
        <Link 
          key={category.id} 
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          data-category-id={category.id} // เพิ่ม data attribute สำหรับ category id
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
