import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useWebGazerContext } from '../contexts/WebGazerContext';

const Sidebar = () => {
  const navigate = useNavigate();
  const { webgazerInstance } = useWebGazerContext();

  useEffect(() => {
    if (!webgazerInstance.current) return;

    const buttons = document.querySelectorAll('.sidebar-button');
    buttons.forEach((button) => {
      button.addEventListener('click', () => {
        const categoryId = button.getAttribute('data-category-id');
        navigate(`/category/${categoryId}`);
      });
    });

    return () => {
      buttons.forEach((button) => {
        button.removeEventListener('click', () => {});
      });
    };
  }, [webgazerInstance, navigate]);

  return (
    <div className="sidebar">
      <button className="sidebar-button" data-category-id="1">Category 1</button>
      <button className="sidebar-button" data-category-id="2">Category 2</button>
      <button className="sidebar-button" data-category-id="3">Category 3</button>
    </div>
  );
};

export default Sidebar;
