import { useEffect, useRef, useCallback } from 'react';
import { useWebGazerContext } from '../contexts/WebGazerContext';

const useWebGazer = (onGaze) => {
  const { webgazerReady, webgazerInstance } = useWebGazerContext();
  const gazeTimers = useRef({});

  useEffect(() => {
    if (webgazerReady && webgazerInstance.current) {
      const listener = (data, elapsedTime) => {
        if (onGaze) {
          onGaze(data, elapsedTime);
        }
      };

      webgazerInstance.current.setGazeListener(listener);

      return () => {
        webgazerInstance.current.removeGazeListener(listener);
      };
    }
  }, [webgazerReady, onGaze, webgazerInstance]);

  return { webgazerReady, webgazerInstance, gazeTimers };
};

export default useWebGazer;
