import React, { useContext, useEffect, useMemo } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sidebar.css';
import icon from '../assets/hospital.png';
import { GazeContext } from '../App'; // Assuming GazeContext is set up in App.js

const Sidebar = () => {
  const location = useLocation();
  const { gazeTimers } = useContext(GazeContext);

  const categories = useMemo(() => [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ], []);

  useEffect(() => {
    const timers = {};

    categories.forEach((category, index) => {
      timers[`category-button-${index}`] = { isGazing: false, timer: null };
    });

    Object.assign(gazeTimers.current, timers);

    // Cleanup function
    return () => {
      Object.keys(timers).forEach(key => {
        if (timers[key].timer) {
          clearTimeout(timers[key].timer);
        }
      });
    };
  }, [categories, gazeTimers]);

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link 
          key={category.id} 
          to={`/category/${category.id}`}
          id={`category-button-${index}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
