// components/Sidebar.js
import React, { useEffect, useRef, useCallback, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWebGazerContext } from '../hooks/WebGazerContext'; // ตรวจสอบเส้นทางให้ถูกต้อง
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const { webgazerInstance, webgazerReady } = useWebGazerContext();
  const categoriesRef = useRef([]);
  const [gazingAt, setGazingAt] = useState(null);
  const gazeTimeout = useRef(null);

  const handleGaze = useCallback((data) => {
    if (!data || !webgazerReady) return;

    const { x, y } = data;
    const links = document.querySelectorAll('.category-button');
    let gazedLink = null;

    links.forEach(link => {
      const rect = link.getBoundingClientRect();
      const isWithinLink = x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom;

      if (isWithinLink) {
        link.classList.add('gazing');
        if (!gazedLink) gazedLink = link;
      } else {
        link.classList.remove('gazing');
      }
    });

    if (gazedLink) {
      if (gazingAt !== gazedLink) {
        setGazingAt(gazedLink);
        if (gazeTimeout.current) {
          clearTimeout(gazeTimeout.current);
        }
        gazeTimeout.current = setTimeout(() => {
          console.log('Gaze click on:', gazedLink.textContent);
          gazedLink.click(); // Simulate a click event
          setGazingAt(null);
        }, 1000);
      }
    } else {
      setGazingAt(null);
      if (gazeTimeout.current) {
        clearTimeout(gazeTimeout.current);
        gazeTimeout.current = null;
      }
    }
  }, [webgazerReady, gazingAt]);

  useEffect(() => {
    if (webgazerReady && webgazerInstance.current) {
      const currentWebgazer = webgazerInstance.current;

      const gazeListener = (data) => {
        handleGaze(data);
      };

      currentWebgazer.setGazeListener(gazeListener);

      return () => {
        if (webgazerReady && currentWebgazer) {
          currentWebgazer.clearGazeListener();
        }
      };
    }
  }, [handleGaze, webgazerReady, webgazerInstance]);

  useEffect(() => {
    const instance = webgazerInstance.current;
    const currentRefs = categoriesRef.current;

    if (instance) {
      currentRefs.forEach((ref) => {
        if (ref) {
          instance.addMouseEventListeners(ref);
        }
      });
    }

    return () => {
      if (instance) {
        currentRefs.forEach((ref) => {
          if (ref) {
            instance.removeMouseEventListeners(ref);
          }
        });
      }
    };
  }, [webgazerInstance]);

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          ref={(el) => (categoriesRef.current[index] = el)}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
