// WebGazerSetup.js
import React, { useEffect } from 'react';
import webgazer from 'webgazer';

const WebGazerSetup = () => {
  useEffect(() => {
    // เริ่มต้นการใช้งาน WebGazer
    webgazer.setRegression('ridge') // ตั้งค่า regression method
      .setGazeListener((data, elapsedTime) => {
        if (data == null) {
          return;
        }
        const x = data.x; // x coordinate of gaze
        const y = data.y; // y coordinate of gaze
        console.log(`Gaze coordinates: (${x}, ${y})`);
      })
      .begin();

    // ซ่อน video feed ของ WebGazer
    webgazer.showVideo(false);
    webgazer.showFaceOverlay(false);
    webgazer.showFaceFeedbackBox(false);

    return () => {
      // หยุดการใช้งาน WebGazer เมื่อ component ถูกทำลาย
      webgazer.end();
    };
  }, []);

  return null;
};

export default WebGazerSetup;
