import React, { useState, useEffect, useRef, useCallback } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import HomePage from './pages/HomePage';
import CategoryPage from './pages/CategoryPage';
import './App.css';

// คอมโพเนนต์สำหรับ App
const App = () => {
  const [webgazerReady, setWebgazerReady] = useState(false);
  const [calibrating, setCalibrating] = useState(false);
  const webgazerInstance = useRef(null);
  const gazeTimers = useRef({});

  useEffect(() => {
    const initWebgazer = async () => {
      try {
        // เริ่มต้น WebGazer
        await window.webgazer.setRegression('ridge').setTracker('clmtrackr').begin();
        webgazerInstance.current = window.webgazer;
        setWebgazerReady(true);

        // แสดงวิดีโอและฟีดแบ็กของใบหน้า
        window.webgazer.showVideo(true);
        window.webgazer.showFaceOverlay(true);
        window.webgazer.showFaceFeedbackBox(true);

      } catch (error) {
        console.error('Failed to initialize webgazer:', error);
      }
    };

    initWebgazer();

    return () => {
      if (webgazerInstance.current && typeof webgazerInstance.current.end === 'function') {
        try {
          webgazerInstance.current.end();
        } catch (error) {
          console.error('Error while ending webgazer:', error);
        }
      }
    };
  }, []);

  // ฟังก์ชันจัดการการมองจาก WebGazer
  const handleGaze = useCallback((data) => {
    if (data == null || calibrating) return;

    const { x, y } = data;
    // ตัวอย่างโค้ดที่สามารถจัดการกับข้อมูลการมอง
    console.log(`Gaze Position: (${x}, ${y})`);
  }, [calibrating]);

  useEffect(() => {
    if (webgazerReady) {
      window.webgazer.setGazeListener(handleGaze);
    }
  }, [webgazerReady, handleGaze]);

  // ฟังก์ชันเริ่มการคาลิเบรต
  const startCalibration = () => {
    if (webgazerInstance.current) {
      webgazerInstance.current.clearData();
      webgazerInstance.current.showFaceOverlay(false);
      setCalibrating(true);
    }
  };

  // ฟังก์ชันหยุดการคาลิเบรต
  const stopCalibration = () => {
    if (webgazerInstance.current) {
      webgazerInstance.current.showFaceOverlay(false);
    }
    setCalibrating(false);
  };

  if (!webgazerReady) {
    return <div>กำลังโหลด WebGazer...</div>;
  }

  return (
    <Router>
      <div className="app-container">
        <Sidebar />
        <div className="main-content">
          <button onClick={startCalibration}>เริ่มคาลิเบรต</button>
          <button onClick={stopCalibration}>หยุดคาลิเบรต</button>
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/category/:id" element={<CategoryPage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
