import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom'; // เปลี่ยนจาก useHistory เป็น useNavigate
import { useWebGazerContext } from '../hooks/WebGazerContext';
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const navigate = useNavigate(); // ใช้ useNavigate แทน useHistory
  const { webgazerInstance } = useWebGazerContext();
  const categoriesRef = useRef([]);
  const gazeTimerRef = useRef(null);
  const [gazingIndex, setGazingIndex] = useState(null);

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  const handleNavigation = useCallback((index) => {
    const category = categories[index];
    if (category) {
      navigate(`/category/${category.id}`);
    }
  }, [navigate]); // ไม่ต้องใช้ categories เป็น dependency เพราะเรากำหนด categories ไว้ด้านนอกแล้ว


  const handleGazeStart = useCallback((index) => {
    setGazingIndex(index);
    gazeTimerRef.current = setTimeout(() => {
      handleNavigation(index);
    }, 1000); // 1 วินาทีในการมองค้าง
  }, [handleNavigation]);

  const handleGazeEnd = useCallback(() => {
    clearTimeout(gazeTimerRef.current);
    setGazingIndex(null);
  }, []);

  useEffect(() => {
    const instance = webgazerInstance.current;
    const currentRefs = categoriesRef.current;

    if (instance) {
      currentRefs.forEach((ref, index) => {
        if (ref) {
          instance.addMouseEventListeners(ref);

          ref.addEventListener('mouseover', () => handleGazeStart(index));
          ref.addEventListener('mouseout', handleGazeEnd);
        }
      });
    }

    return () => {
      if (instance) {
        currentRefs.forEach((ref) => {
          if (ref) {
            instance.removeMouseEventListeners(ref);
            ref.removeEventListener('mouseover', handleGazeStart);
            ref.removeEventListener('mouseout', handleGazeEnd);
          }
        });
      }
    };
  }, [webgazerInstance, handleGazeStart, handleGazeEnd]);

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''} ${gazingIndex === index ? 'gazing' : ''}`}
          ref={(el) => (categoriesRef.current[index] = el)}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
