import React, { useRef, useEffect, createContext } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import HomePage from './pages/HomePage';
import CategoryPage from './pages/CategoryPage';
import CalibrationPoint from './components/CalibrationPoint';
import useWebGazer from './components/WebGazer';
import './App.css';

// Remove the import WebGazer statement
// import WebGazer from 'webgazer';

export const GazeContext = createContext({});

const GazeHandler = ({ gazeTimers }) => {
  useEffect(() => {
    // Use webgazer directly from the global object
    window.webgazer.setGazeListener((data) => {
      if (data == null) {
        return;
      }

      const { x, y } = data;
      const element = document.elementFromPoint(x, y);
      if (element && element.id && gazeTimers.current[element.id]) {
        const timerData = gazeTimers.current[element.id];
        if (!timerData.isGazing) {
          timerData.isGazing = true;
          timerData.timer = setTimeout(() => {
            element.click();
            timerData.isGazing = false;
          }, 1000); // Adjust gaze duration as needed
        }
      }
    }).begin();

    return () => {
      window.webgazer.end();
    };
  }, [gazeTimers]);

  return null;
};

const App = () => {
  const gazeTimers = useRef({});
  const {
    calibrating,
    calibrationPoints,
    webgazerReady,
    startCalibration,
    stopCalibration,
    handleCalibrationClick
  } = useWebGazer();

  if (!webgazerReady) {
    return <div>กำลังโหลด WebGazer...</div>;
  }

  return (
    <GazeContext.Provider value={{ gazeTimers, calibrating }}>
      <GazeHandler gazeTimers={gazeTimers} />
      <Router>
        <div className="app-container">
          <Sidebar />
          <div className="main-content">
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/category/:id" element={<CategoryPage />} />
            </Routes>
            <div className="calibration-container">
              <button onClick={startCalibration}>เริ่มคาลิเบรต</button>
              <button onClick={stopCalibration}>หยุดคาลิเบรต</button>
            </div>
            {calibrating && (
              <div className="calibration-points">
                {calibrationPoints.map((point, index) => (
                  <CalibrationPoint 
                    key={index}
                    point={point}
                    index={index}
                    handleCalibrationClick={handleCalibrationClick}
                  />
                ))}
              </div>
            )}
          </div>
        </div>
      </Router>
    </GazeContext.Provider>
  );
};

export default App;
