// src/pages/CategoryPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import './CategoryPage.css';
import deleteIcon from '../assets/delete.png';
import alertIcon from '../assets/bell.png';
import WebGazerWrapper from '../components/WebGazerWrapper';

const CategoryPage = () => {
  const { id: paramId } = useParams();
  const id = paramId || 'หน้าหลัก'; 
  const [message, setMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [letters, setLetters] = useState([]);
  const [defaultLetters] = useState([
    'ก-จ', 'ฉ-ฐ', 'ฒ-ธ', 'น-ภ', 'ม-ส', 'ห-ฮ'
  ]);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const letterButtonsRef = useRef([]);

  useEffect(() => {
    const letterMap = {
      'หน้าหลัก': ['สวัสดี', 'หิวข้าว', 'เข้าห้องน้ำ', 'สบายดีไหม', 'ขอบคุณ', 'ขอโทษ', 'คิดถึง', 'รัก', 'ไปไหน'],
      'พยัญชนะ': defaultLetters,
      'สระ': ['สระ1', 'สระ2'],
      'วรรณยุกต์': ['่', '้', '๊', '๋', '็', '์'],
    };

    if (id === 'พยัญชนะ') {
      setSelectedSubcategory(null);
    }

    setLetters(letterMap[id] || defaultLetters);
  }, [id, defaultLetters]);

  const handleGaze = (prediction) => {
    letterButtonsRef.current.forEach((button) => {
      if (button) {
        const rect = button.getBoundingClientRect();
        const x = prediction.x;
        const y = prediction.y;

        if (x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom) {
          setGazingButton(button);
        }
      }
    });
  };

  useEffect(() => {
    if (gazingButton) {
      const timer = setTimeout(() => {
        if (gazingButton) {
          gazingButton.click();
        }
      }, 1000); // 1 วินาที

      return () => clearTimeout(timer);
    }
  }, [gazingButton]);

  return (
    <div className="category-page">
      <WebGazerWrapper onGaze={handleGaze} />
      <h2>{id}</h2>

      <div className="message-input">
        <input 
          type="text" 
          value={message} 
          onChange={(e) => setMessage(e.target.value)} 
          placeholder="พิมพ์ข้อความ"
        />
        <button onClick={handleSend}>ตกลง</button>
      </div>
      <div className="letter-grid">
        {letters.map((letter, index) => (
          <button 
            key={index} 
            className="letter-button"
            onClick={() => handleLetterClick(letter)}
            ref={(el) => {
              if (el) {
                letterButtonsRef.current[index] = el;
              }
            }}
          >
            {letter}
          </button>
        ))}
        {!(id === 'หน้าหลัก' || (id === 'สระ' && (letters.includes('สระ1') || letters.includes('สระ2'))) || (id === 'พยัญชนะ' && letters === defaultLetters)) && (
          <button 
            key="delete" 
            className="letter-button delete-button"
            onClick={() => setMessage(prevMessage => prevMessage.slice(0, -1))}
          >
            <img src={deleteIcon} alt="delete" className="delete-icon" />
          </button>
        )}
      </div>
      <img 
        src={alertIcon} 
        alt="alert" 
        className="alert-icon" 
        onClick={handleAlertClick} 
      />
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <p>ต้องการเรียกผู้ดูแลหรือไม่?</p>
            <button onClick={handleConfirmAlert}>ใช่</button>
            <button onClick={handlePopupClose}>ไม่ใช่</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryPage;
