import React, { useEffect, useRef, useCallback, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWebGazerContext } from '../hooks/WebGazerContext'; 
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const { webgazerInstance, webgazerReady } = useWebGazerContext();
  const categoriesRef = useRef([]);
  const [gazingAt, setGazingAt] = useState(null);
  const [calibrating, setCalibrating] = useState(false);
  const gazeTimeout = useRef(null);

  const handleGaze = useCallback((data) => {
    if (data == null || !categoriesRef.current.length) return;

    const { x, y } = data;
    const buttons = categoriesRef.current;
    let gazedButton = null;

    buttons.forEach(button => {
      if (button) {
        const rect = button.getBoundingClientRect();
        const isWithinButton = x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom;

        if (isWithinButton) {
          button.classList.add('gazing');
          if (!gazedButton) gazedButton = button;
        } else {
          button.classList.remove('gazing');
        }
      }
    });

    if (gazedButton) {
      if (gazingAt !== gazedButton) {
        setGazingAt(gazedButton);
        if (gazeTimeout.current) {
          clearTimeout(gazeTimeout.current);
        }
        gazeTimeout.current = setTimeout(() => {
          console.log('Gaze click on:', gazedButton.textContent);
          gazedButton.click();
          setGazingAt(null);
        }, 1000);
      }
    } else {
      setGazingAt(null);
      if (gazeTimeout.current) {
        clearTimeout(gazeTimeout.current);
        gazeTimeout.current = null;
      }
    }
  }, [gazingAt]);

  const startCalibration = () => {
    if (webgazerInstance.current) {
      webgazerInstance.current.clearData();
      webgazerInstance.current.showFaceOverlay(true);
      webgazerInstance.current.showVideo(true);
      setCalibrating(true);
    }
  };

  const stopCalibration = () => {
    if (webgazerInstance.current) {
      webgazerInstance.current.showFaceOverlay(false);
      webgazerInstance.current.showVideo(false);
    }
    setCalibrating(false);
  };

  useEffect(() => {
    const instance = webgazerInstance.current;
    const currentRefs = categoriesRef.current;

    if (instance && webgazerReady) {
      const gazeListener = (data) => {
        handleGaze(data);
      };

      instance.setGazeListener(gazeListener);

      return () => {
        if (instance) {
          currentRefs.forEach((ref) => {
            if (ref) {
              instance.removeMouseEventListeners(ref);
            }
          });
          instance.clearGazeListener();
        }
      };
    }
  }, [webgazerInstance, webgazerReady, handleGaze]);

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      <div className="calibration-container">
        <button onClick={startCalibration} disabled={calibrating}>เริ่มคาลิเบรต</button>
        <button onClick={stopCalibration} disabled={!calibrating}>หยุดคาลิเบรต</button>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          ref={(el) => (categoriesRef.current[index] = el)}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
