import React, { useEffect, useRef, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWebGazerContext } from '../hooks/WebGazerContext';
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const { webgazerInstance } = useWebGazerContext();
  const categoriesRef = useRef([]);
  const [activeButton, setActiveButton] = useState(null);
  const [timer, setTimer] = useState(null);

  useEffect(() => {
    const instance = webgazerInstance.current;
    const currentRefs = categoriesRef.current;

    const handleGaze = (data) => {
      if (data && data.x && data.y) {
        const { x, y } = data;
        currentRefs.forEach((ref, index) => {
          if (ref) {
            const rect = ref.getBoundingClientRect();
            const isGazing = x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom;

            if (isGazing) {
              if (activeButton !== index) {
                setActiveButton(index);
                if (timer) clearTimeout(timer);

                const newTimer = setTimeout(() => {
                  ref.click(); // Trigger click event
                }, 1000); // 1 วินาที

                setTimer(newTimer);

                // เพิ่มการแสดงแถบโหลด
                ref.querySelector('.loading-bar').style.width = '100%';
              }
            } else {
              if (activeButton === index) {
                setActiveButton(null);
                if (timer) clearTimeout(timer);

                // รีเซ็ตแถบโหลด
                ref.querySelector('.loading-bar').style.width = '0';
              }
            }
          }
        });
      }
    };

    if (instance) {
      instance.setGazeListener(handleGaze).begin();
    }

    return () => {
      if (instance) {
        instance.end();
      }
    };
  }, [webgazerInstance, activeButton, timer]);

  // useEffect นี้จะรีเซ็ตสถานะ activeButton เมื่อมีการเปลี่ยนแปลงของเส้นทาง
  useEffect(() => {
    if (location.pathname === '/category/หน้าหลัก') {
      // รีเซ็ตหมวดหมู่กลับไปที่ค่าเริ่มต้น
      setActiveButton(null);
    }
  }, [location]);

  const categories = [
    { id: 'หน้าหลัก', label: 'หน้าหลัก' },
    { id: 'พยัญชนะ', label: 'พยัญชนะ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          ref={(el) => {
            if (el) {
              categoriesRef.current[index] = el;
            }
          }}
        >
          <div className="loading-bar"></div> {/* แถบโหลด */}
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
