import React, { useEffect, useRef, useCallback } from 'react';
import { Link, useLocation, useHistory } from 'react-router-dom';
import { useWebGazerContext } from '../hooks/WebGazerContext';
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const history = useHistory();
  const { webgazerInstance } = useWebGazerContext();
  const categoriesRef = useRef([]);
  const gazeTimeout = useRef(null);

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  const handleGaze = useCallback((data) => {
    if (data == null) return;

    const { x, y } = data;
    let gazedCategory = null;

    categoriesRef.current.forEach((ref, index) => {
      if (ref) {
        const rect = ref.getBoundingClientRect();
        const isWithinCategory = x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom;

        if (isWithinCategory) {
          ref.classList.add('gazing');
          gazedCategory = categories[index];
        } else {
          ref.classList.remove('gazing');
        }
      }
    });

    if (gazedCategory) {
      if (gazeTimeout.current) clearTimeout(gazeTimeout.current);
      gazeTimeout.current = setTimeout(() => {
        history.push(`/category/${gazedCategory.id}`);
      }, 1000);
    } else if (gazeTimeout.current) {
      clearTimeout(gazeTimeout.current);
      gazeTimeout.current = null;
    }
  }, [history]);

  useEffect(() => {
    const instance = webgazerInstance.current;

    if (instance) {
      instance.setGazeListener(handleGaze).begin();
    }

    return () => {
      if (instance) {
        instance.clearGazeListener();
      }
      if (gazeTimeout.current) {
        clearTimeout(gazeTimeout.current);
        gazeTimeout.current = null;
      }
    };
  }, [webgazerInstance, handleGaze]);

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          ref={(el) => (categoriesRef.current[index] = el)}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
