import React, { useState, useCallback, useEffect, useRef } from 'react';
import './HomePage.css';
import alertIcon from '../assets/bell.png'; 
import { useWebGazerContext } from '../hooks/WebGazerContext';

const HomePage = () => {
  const [message, setMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const { webgazerInstance } = useWebGazerContext();

  const [gazeStartTime, setGazeStartTime] = useState(null);
  const [gazedButton, setGazedButton] = useState(null);
  const [calibrating, setCalibrating] = useState(false);
  const [calibrationPoints, setCalibrationPoints] = useState([]);
  const [clickCounts, setClickCounts] = useState(Array(5).fill(0)); // เปลี่ยนขนาดของ clickCounts ให้ตรงกับจำนวนปุ่มปรับเทียบ
  const [calibrationActive, setCalibrationActive] = useState(false);

  const gazeTimeout = useRef(null);

  const suggestions = ['สวัสดี', 'หิวข้าว', 'เข้าห้องน้ำ', 'สบายดีไหม', 'ขอบคุณ', 'ขอโทษ', 'คิดถึง', 'รัก', 'ไปไหน'];

  const startCalibration = () => {
    if (webgazerInstance.current) {
      webgazerInstance.current.clearData();
      webgazerInstance.current.showFaceOverlay(false);
      setCalibrating(true);
      setCalibrationPoints(generateCalibrationPoints());
      setClickCounts(Array(5).fill(0)); // ปรับจำนวนปุ่มตามที่ใช้จริง
    }
  };

  const stopCalibration = () => {
    if (webgazerInstance.current) {
      webgazerInstance.current.showFaceOverlay(false);
    }
    setCalibrating(false);
    setCalibrationPoints([]);
  };

  const generateCalibrationPoints = () => {
    // กำหนดตำแหน่งจุดปรับเทียบที่นี่
    const positions = [
      { x: 10, y: 20 }, { x: 30, y: 40 }, { x: 50, y: 60 }, { x: 70, y: 80 }, { x: 90, y: 20 },
    ];
    return positions.map(pos => ({ x: `${pos.x}%`, y: `${pos.y}%`, color: 'red' }));
  };

  const handleCalibrationClick = (index, e) => {
    if (webgazerInstance.current) {
      const { clientX, clientY } = e;
      webgazerInstance.current.recordScreenPosition(clientX, clientY, 'click');
      const newClickCounts = [...clickCounts];
      newClickCounts[index] += 1;
      setClickCounts(newClickCounts);

      if (newClickCounts[index] >= 3) {
        const newCalibrationPoints = [...calibrationPoints];
        newCalibrationPoints[index].color = 'yellow';
        setCalibrationPoints(newCalibrationPoints);
      }
    }
  };

  const handleGaze = useCallback((data) => {
    if (data == null || calibrating) return;

    const { x, y } = data;
    const buttons = document.querySelectorAll('.suggestion-button');
    let currentGazedButton = null;

    buttons.forEach(button => {
      const rect = button.getBoundingClientRect();
      if (
        x >= rect.left &&
        x <= rect.right &&
        y >= rect.top &&
        y <= rect.bottom
      ) {
        button.classList.add('gazing');
        currentGazedButton = button;
      } else {
        button.classList.remove('gazing');
      }
    });

    if (currentGazedButton) {
      if (!gazedButton) {
        setGazedButton(currentGazedButton);
        setGazeStartTime(Date.now());
      } else if (gazedButton === currentGazedButton && Date.now() - gazeStartTime > 1000) {
        setMessage(currentGazedButton.innerText);
        setGazedButton(null);
        setGazeStartTime(null);
      }
    } else {
      setGazedButton(null);
      setGazeStartTime(null);
    }
  }, [gazedButton, gazeStartTime, calibrating]);

  useEffect(() => {
    const instance = webgazerInstance.current;
    if (instance) {
      instance.addGazeListener && instance.addGazeListener(handleGaze);
    }

    return () => {
      if (instance) {
        instance.removeGazeListener && instance.removeGazeListener(handleGaze);
      }
    };
  }, [handleGaze, webgazerInstance]);

  return (
    <div className="home-page">
      <div className="calibration-buttons">
        <button onClick={startCalibration} disabled={calibrating}>เริ่มปรับเทียบ</button>
        <button onClick={stopCalibration} disabled={!calibrating}>หยุดปรับเทียบ</button>
      </div>
      <div className="message-input">
        <input 
          type="text" 
          value={message} 
          onChange={(e) => setMessage(e.target.value)} 
          placeholder="พิมพ์ข้อความ"
        />
        <button onClick={handleSend}>ตกลง</button>
      </div>
      <div className="suggestions">
        {suggestions.map((suggestion, index) => (
          <button 
            key={index} 
            onClick={() => setMessage(suggestion)}
            className="suggestion-button"
          >
            {suggestion}
          </button>
        ))}
      </div>
      <img 
        src={alertIcon} 
        alt="alert" 
        className="alert-icon" 
        onClick={() => setShowPopup(true)} 
      />
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <p>ต้องการเรียกผู้ดูแลหรือไม่?</p>
            <button onClick={handleConfirmAlert}>ใช่</button>
            <button onClick={() => setShowPopup(false)}>ไม่ใช่</button>
          </div>
        </div>
      )}
      <div className="calibration-points">
        {calibrationPoints.map((point, index) => (
          <button
            key={index}
            className="calibration-point"
            style={{ 
              position: 'absolute', 
              left: point.x, 
              top: point.y, 
              backgroundColor: point.color 
            }}
            onClick={(e) => handleCalibrationClick(index, e)}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
