import React, { useEffect, useRef, useCallback, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWebGazerContext } from '../hooks/WebGazerContext';
import './Sidebar.css';
import icon from '../assets/hospital.png';

const Sidebar = () => {
  const location = useLocation();
  const { webgazerInstance } = useWebGazerContext();
  const categoriesRef = useRef([]);
  const [gazingAt, setGazingAt] = useState(null);
  const gazeTimeout = useRef(null);

  const categories = [
    { id: 'ก-ซ', label: 'ก-ซ' },
    { id: 'ฌ-ถ', label: 'ฌ-ถ' },
    { id: 'ท-ม', label: 'ท-ม' },
    { id: 'ย-ฮ', label: 'ย-ฮ' },
    { id: 'สระ', label: 'สระ' },
    { id: 'วรรณยุกต์', label: 'วรรณยุกต์' },
  ];

  const handleGaze = useCallback((data) => {
    if (!data) return;

    const { x, y } = data;
    const buttons = document.querySelectorAll('.category-button');
    let gazedButton = null;

    buttons.forEach(button => {
      const rect = button.getBoundingClientRect();
      const isWithinButton = x >= rect.left && x <= rect.right && y >= rect.top && y <= rect.bottom;

      if (isWithinButton) {
        button.classList.add('gazing');
        if (!gazedButton) gazedButton = button;
      } else {
        button.classList.remove('gazing');
      }
    });

    if (gazedButton) {
      if (gazingAt !== gazedButton) {
        setGazingAt(gazedButton);
        if (gazeTimeout.current) {
          clearTimeout(gazeTimeout.current);
        }
        gazeTimeout.current = setTimeout(() => {
          console.log('Gaze click on:', gazedButton.textContent);
          window.location.href = gazedButton.href; // Change page based on the href of the gazed button
          setGazingAt(null);
        }, 1000);
      }
    } else {
      setGazingAt(null);
      if (gazeTimeout.current) {
        clearTimeout(gazeTimeout.current);
        gazeTimeout.current = null;
      }
    }
  }, [gazingAt]);

  useEffect(() => {
    const instance = webgazerInstance.current;
    const currentRefs = categoriesRef.current;

    if (instance) {
      const gazeListener = (data, elapsedTime) => {
        console.log('Gaze data:', data); // Debug gaze data
        handleGaze(data);
      };

      instance.setGazeListener(gazeListener);
    }

    return () => {
      if (instance) {
        currentRefs.forEach((ref) => {
          if (ref) {
            instance.removeMouseEventListeners(ref);
          }
        });
        instance.clearGazeListener();
      }
    };
  }, [webgazerInstance, handleGaze]);

  return (
    <div className="sidebar">
      <div className="logo">
        <Link to="/" className="logo-link">
          <div>
            <img src={icon} alt="Icon" style={{ width: '70px', height: '70px', marginRight: '20px' }} />
          </div>
          <div className="logo-text">GazeTalk</div>
        </Link>
      </div>
      {categories.map((category, index) => (
        <Link
          key={category.id}
          to={`/category/${category.id}`}
          className={`category-button ${location.pathname === `/category/${category.id}` ? 'active' : ''}`}
          ref={(el) => (categoriesRef.current[index] = el)}
        >
          {category.label}
        </Link>
      ))}
    </div>
  );
};

export default Sidebar;
