import React, { createContext } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import HomePage from './pages/HomePage';
import CategoryPage from './pages/CategoryPage';
import CalibrationPoint from './components/CalibrationPoint';
import useWebGazer from './components/WebGazer';
import './App.css';

export const GazeContext = createContext();

const App = () => {
  const {
    calibrating,
    calibrationPoints,
    webgazerReady,
    gazeTimers,
    startCalibration,
    stopCalibration,
    handleCalibrationClick
  } = useWebGazer();

  if (!webgazerReady) {
    return <div>กำลังโหลด WebGazer...</div>;
  }

  return (
    <GazeContext.Provider value={{ gazeTimers, calibrating }}>
      <Router>
        <div className="app-container">
          <Sidebar />
          <div className="main-content">
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/category/:id" element={<CategoryPage />} />
            </Routes>
            <div className="calibration-container">
              <button onClick={startCalibration}>เริ่มคาลิเบรต</button>
              <button onClick={stopCalibration}>หยุดคาลิเบรต</button>
            </div>
            {calibrating && (
              <div className="calibration-points">
                {calibrationPoints.map((point, index) => (
                  <CalibrationPoint 
                    key={index}
                    point={point}
                    index={index}
                    handleCalibrationClick={handleCalibrationClick}
                  />
                ))}
              </div>
            )}
          </div>
        </div>
      </Router>
    </GazeContext.Provider>
  );
};

export default App;
