import React, { createContext, useContext, useState, useRef, useCallback, useEffect } from 'react';

const WebGazerContext = createContext();

export const WebGazerProvider = ({ children }) => {
  const [webgazerReady, setWebgazerReady] = useState(false);
  const webgazerInstance = useRef(null);

  const initializeWebGazer = useCallback(async () => {
    try {
      await window.webgazer.setRegression('ridge').setTracker('clmtrackr').begin();
      webgazerInstance.current = window.webgazer;
      setWebgazerReady(true);

      // ตั้งค่าให้ซ่อน UI ของ WebGazer
      window.webgazer.showVideo(true);
      window.webgazer.showFaceOverlay(false);
      window.webgazer.showFaceFeedbackBox(true);

      // ซ่อนผ่านการใช้ CSS หากยังไม่ถูกซ่อน
      document.getElementById('webgazerVideoFeed').style.display = 'none';
      document.getElementById('webgazerFaceOverlay').style.display = 'none';
      document.getElementById('webgazerFaceFeedbackBox').style.display = 'none';

      // เพิ่มฟังก์ชันสำหรับการเลื่อนหน้าจอ
      const checkGazePosition = () => {
        const prediction = webgazerInstance.current.getCurrentPrediction();
        if (prediction) {
          const { x, y } = prediction; // ตำแหน่งการจ้องมอง (x, y)
          const windowHeight = window.innerHeight;

          // เลื่อนขึ้นถ้าผู้ใช้มองใกล้กับขอบบนของหน้าจอ
          if (y < windowHeight * 0.1) {
            window.scrollBy({ top: -10, behavior: 'smooth' });
          }
          // เลื่อนลงถ้าผู้ใช้มองใกล้กับขอบล่างของหน้าจอ
          else if (y > windowHeight * 0.9) {
            window.scrollBy({ top: 10, behavior: 'smooth' });
          }
        }
      };

      // ตั้งเวลาให้เรียกฟังก์ชัน checkGazePosition ทุกๆ 100ms
      const intervalId = setInterval(checkGazePosition, 100);

      return () => clearInterval(intervalId); // เคลียร์ interval เมื่อ component ถูกถอดออก
    } catch (error) {
      console.error('Failed to initialize webgazer:', error);
    }
  }, []);

  useEffect(() => {
    initializeWebGazer();

    return () => {
      if (webgazerInstance.current && typeof webgazerInstance.current.end === 'function') {
        try {
          webgazerInstance.current.end();
        } catch (error) {
          console.error('Error while ending webgazer:', error);
        }
      }
    };
  }, [initializeWebGazer]);

  return (
    <WebGazerContext.Provider value={{ webgazerReady, webgazerInstance }}>
      {children}
    </WebGazerContext.Provider>
  );
};

export const useWebGazerContext = () => {
  const context = useContext(WebGazerContext);
  if (!context) {
    throw new Error('useWebGazerContext must be used within a WebGazerProvider');
  }
  return context;
};
